import { IoIosTrash,IoMdCreate } from "react-icons/io"
import toast from 'react-hot-toast'
import { usePosts } from "../context/postContext";
import { useNavigate } from "react-router-dom";

export function PostCard({ post }) {

    const {deletePost} = usePosts()
    const navigate = useNavigate()


    const handleDelete = (id) => {
        toast((t) => (
            <div>
                <div className="text-black px-3 py-3 mx-3">Â¿Desea eliminar esta publicacion?</div>
                <div className="text-black px-3 py-3 mx-3">{id}</div>
                <div className="flex justify-between">
                    <button onClick={() => toast.dismiss(t.id)}>Cancelar</button>
                    <button onClick={() => {
                        deletePost(id)
                        toast.dismiss(t.id)
                    }}
                        className="bg-red-500 hover:bg-red-400 px-3 py-2 text-sm text-white rounded-md mx-2"
                    >Eliminar</button>
                </div>
            </div>
        ), {
            style: {
                background: "202020"
            }
        });
    }

    return (
        // <div className="bg-zinc-800 text-white rounded-sm shadow-md shadow-black hover:bg-zinc-700 hover:cursor-pointer">
        //     <div className="px-4 py-7">
        //         <div className="flex justify-between">
        //             <h3>
        //                 {post.title}
        //             </h3>
        //             <div>
        //                 <button onClick={() => navigate(`/posts/${post._id}`)}><IoMdCreate className="w-5 h-5 text-white" /></button>
        //                 <button onClick={(e) => {
        //                     e.stopPropagation()
        //                     handleDelete(post._id)
        //                 }}><IoIosTrash className="w-5 h-5 text-white" /></button>
        //             </div>
        //         </div>

        //         <p>
        //             {post.description}
        //         </p>

        //         {post.image && <img alt="publicaction" src={post.image.url}/>}
        //     </div>
        // </div>
<div className="bg-white rounded-lg shadow-md overflow-hidden">
      <div className="relative h-48">
      {post.image && 
        <img className="absolute top-0 left-0 w-full h-full object-cover" src={post.image.url} alt={post.title} />
      }
      </div>
      <div className="p-6">
        <div className="flex items-center mb-4">
          <div className="flex-shrink-0">
            <img className="h-10 w-10 rounded-full" src="https://via.placeholder.com/150" alt="Avatar" />
          </div>
          <div className="ml-3">
            <div className="text-base font-medium text-gray-800">{post.title}</div>
            <div className="text-sm font-medium text-gray-500">@username</div>
          </div>
        </div>
        <div className="text-lg font-medium mb-4">{post.description}</div>
        <div className="flex items-center">
          <svg className="h-6 w-6 text-gray-400 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M20 6L9 17l-5-5" />
          </svg>
          <div className="text-gray-500">1.2k</div>
          <svg className="h-6 w-6 text-gray-400 ml-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M20 6L9 17l-5-5" />
          </svg>
          <div className="text-gray-500">3.4k</div>
        </div>
      </div>
    </div>
    )
}
