import { IoIosTrash } from "react-icons/io"
import toast from 'react-hot-toast'

export function PostCard({ post }) {

    const handleDelete = () => {
        toast((t) => (
            <div>
              <div className="text-white">Â¿Desea eliminar la publicacion?</div>
              <div>
                <button onClick={() => {
                  toast.dismiss(t.id);
                }}>Eliminar</button>
                <button onClick={() => toast.dismiss(t.id)}>Cancelar</button>
              </div>
            </div>
          ),{
            style:{
                background:"202020"
            }
          });
    }

    return (
        <div className="bg-zinc-800 text-white rounded-sm shadow-md shadow-black hover:bg-zinc-700 hover:cursor-pointer">
            <div className="px-4 py-7">
                <div className="flex justify-between">
                    <h3>
                        {post.title}
                    </h3>
                    <button onClick={handleDelete}><IoIosTrash className="w-5 h-5 text-white" /></button>
                </div>

                <p>
                    {post.description}
                </p>
            </div>
        </div>
    )
}
