import { useState, createContext, useContext, useEffect } from "react"
import { createPostsRequests, deletePostsRequests, getPostRequests, getPostsRequests } from "../api/posts"

export const postContext = createContext()

export const usePosts = () => {
    const context = useContext(postContext)
    return context
}

export const PostProvider = ({ children }) => {

    const [posts, setPosts] = useState([])

    const getPosts = async () => {
        const res = await getPostsRequests()
        setPosts(res.data)
    }
    const getPost = async (_id) => {
        const res = await getPostRequests(_id)
        setPosts(res.data)
    }
    const createPost = async (post) => {
        const res = await createPostsRequests(post)
        setPosts([...posts,res.data])
    }

    const deletePost = async(id) => {
        const res = await deletePostsRequests(id)
        if (res.status === 200 ){
            setPosts(posts.filter(post => post._id !== id))
        }
    }
    useEffect(() => {
        getPosts()
    }, [])


    console.log(posts)
    return <postContext.Provider value={{
        posts,
        getPosts,
        createPost,
        deletePost,
        getPost
    }}>
        {children}
    </postContext.Provider>
}
